import GhibliSelect from '../components/FilmSelect';
import GhibliDetail from '../components/FilmDetail';

const GhibliContainer = () => {
    const [film, setFilm] = useState([]);
    const [selectedFilm, setSelectedFilm] = useState(null);

    useEffect(() => {
        fetch("https://ghibliapi.herokuapp.com/films")
            .then(res => res.json())
            .then(data => setSelectedFilm(data.results))
    }



    )
    return (
        <>
            <h1>Studio Ghibli!</h1>

            <GhibliSelect />
        </>
    );
}

export default GhibliContainer;
